import "./App.css";
// import Components
import Form from './components/Form';
import TodoList from './components/TodoList';

function App() {
  const [inputText, setInputText] = useState("");
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState("all");
  const [filteredTodos, setFilteredTodos] = useState([]);

  //Use Effect
  useEffect (() => {
    filterHandler();
  }, [todos, status]);


  return (
    <div className="App">
      <header>
        <h1>Santi's Todo List</h1>
      </header>
      <Form
        inputText={inputText}
        todos={todos}
        setTodos={setTodos}
        setInputText={setInputText} 
        setStatus={setStatus}
        filteredTodos={filteredTodos}
      />
      <TodoList 
        setTodos={setTodos} 
        todos={todos} 
      />
    </div>
  );
}

export default App;
